import{l as e,f as l,g as t,s as r}from"./framework.5c8a4622.js";const a='{"title":"写作初衷","description":"","frontmatter":{},"headers":[{"level":2,"title":"写作初衷","slug":"写作初衷"}],"relativePath":"index.md","lastUpdated":1628927998788}',n={},u=t("h2",{id:"写作初衷"},[t("a",{class:"header-anchor",href:"#写作初衷","aria-hidden":"true"},"#"),r(" 写作初衷")],-1),s=t("p",null,[r("关于本书《 Vue3 源码解读》，写作的初衷可以说是兴趣使然。因为，读的学校和专业的缘故，平常缺少交流技术的朋友，所以，我很早就开始写博客。也因此对写博客建立起了浓浓的兴趣，因为这个过程可以帮助我发现在学习"),t("strong",null,"任何一个技术过程中存在的不足"),r("，所获良多。")],-1),i=t("p",null,"但是，在写博客的过程，我发现一个问题，就是很多我写的博客知识点都是分散的，例如 HTTP、浏览器原理、前端、源码相关等等。而这些博客很多时候只能代表我研究的东西多，但是并不能代表我研究的够深。所以，在某一个时间点，我打算开始写一本针对某一项技术的电子书。",-1),o=t("p",null,"其次，至于为什么写关于「Vue3」源码的电子书，是因为 Vue 是我从开始学习前端到现在一直在用的框架。并且，在一定程度上，我对「Vue2.x」版本的源码也算是熟悉。而且，「Vue3」做了很多突破性的改善，例如静态节点提升、靶向更新、重写 Vritual DOM、Composition API 设计等等，都是非常值得深入研究和学习的知识点。所以，不可避免地是我对这些也都充满了兴趣。",-1),d=t("p",null,[r("因此，综上所述，我开始了《 Vue3 源码解读》电子书的写作，"),t("strong",null,"也是梦的开始"),r("。")],-1);n.render=function(t,r,a,n,p,f){return e(),l("div",null,[u,s,i,o,d])};export default n;export{a as __pageData};
